import React, { Component } from 'react';
import { Grid } from 'semantic-ui-react'
import 'semantic-ui-css/semantic.min.css';
// import logo from './logo.svg';
import './App.css';
import  StockTable from './StockTable';
import  SearchBar from './SearchBar';
import  NewsFeed  from "./NewsFeed";
import Chart from './Chart';
import axios from 'axios';



class App extends Component {
  
  constructor(props){
    super(props);
    this.state = {
      companyData: []
    }
    this._var1 = 0;
  }

  componentDidMount(){
    this.retrieveData();
  }

  getData = async () => {
    try {
      return await axios.get('https:/localhost:3000/data')
    } catch (error) {
      console.error(error)
    }
  }
  
  retrieveData = async () => {
    const responseFromServer = await this.getData()
  
    if (responseFromServer.data) {
      console.log(responseFromServer.data)
    }
  }
  
  render() {
    return (
      <div className="App">
    
        <SearchBar /> 
          <Grid columns={2}>
          <Grid.Row>
            <Grid.Column>
              <NewsFeed />
              <Chart />
            </Grid.Column>
            <Grid.Column>
             <StockTable /> 
            </Grid.Column>
          </Grid.Row>
        </Grid>  
      </div>


    );
  }
}

export default App;
