import React, { Component } from 'react';
import './index.css';
import 'semantic-ui-css/semantic.min.css';

import { Header, Table, Container } from 'semantic-ui-react'
class StockTable extends Component {

  renderTable = () => {
    let companies = [
        { 
            CompanyName : "Bloomberg",
            SemanticScore : 1.25
        },
        { 
            CompanyName : "Apple",
            SemanticScore : -2
        },
        { 
            CompanyName : "Google",
            SemanticScore : -1.7
        },
        { 
            CompanyName : "Epic",
            SemanticScore : -1.25
        }]
    return (
        <div>
            <Container>
                <Table basic='very' celled collapsing>
                    <Table.Header>
                        <Table.Row>
                            <Table.HeaderCell>Companies</Table.HeaderCell>
                            <Table.HeaderCell>Sentiments</Table.HeaderCell>
                        </Table.Row>
                    </Table.Header>
                    <Table.Body>
                        {companies.map((el, index) => (
                            <div>
                                <Table.Row>
                                    <Table.Cell>
                                    <Header as='h4' >
                                        {/* <Image src = '../logo.svg' rounded size='mini' /> */}
                                        <Header.Content>
                                        {el.CompanyName}
                                        <Header.Subheader>Human Resources</Header.Subheader>
                                        </Header.Content>
                                    </Header>
                                    </Table.Cell>
                                    <Table.Cell>
                                        <Header.Content>
                                        {el.SemanticScore}
                                        </Header.Content>
                                        
                                    </Table.Cell>
                                </Table.Row>
                            </div>
                        ))}
                    </Table.Body>
                </Table>
            </Container>
        </div>
    )
  }
  render() {
    return (   
        <div>
            {this.renderTable()}
        </div>
    );
  }
}

export default StockTable;
